<template>
  <div class="flex-grow-1">
    <div class="d-flex align-center">
      <div>
        <!-- 画面タイトル -->
        <div class="display-1">
          {{ "フィルターサンプル詳細" }}
        </div>
        <!-- パンくずリスト -->
        <a-breadcrumbs :items="breadcrumbs" />
      </div>
    </div>

    <v-card>
        <v-toolbar dense flat>
          <v-toolbar-title>{{ "プロフィール" }}</v-toolbar-title>
          <v-spacer></v-spacer>
        </v-toolbar>
        <v-divider></v-divider>
        <v-card-text>
          <div class="d-flex flex-row">
            <div class="d-flex flex-column pr-3 align-center">
          <!-- 写真 -->
          <img width="65px" height="77px" src="data:image/png;base64,">
          <label>{{ "勤続年数" }}</label>
          <v-avatar
            color="primary"
            size="36"
          >
          <span class="white--text text-h5">{{ lengthOfService }}</span>
          </v-avatar>
            </div>
            <div class="flex-grow-1">
              <h5>{{ "ユーザー情報" }}</h5>
          <!-- 社員番号 -->
          <a-label-text-field
            vid="employeeNo"
            ref="employeeNo"
            v-model="employeeNo"
            label="社員番号"
            disabled="true"
            outlined
            dense
          />
          <a-label-date-select
            label="誕生日"
            outlined
            dense
          />
          <!-- 社員名 -->
          <a-label-text-field
            vid="userName"
            ref="userName"
            v-model="userName"
            label="社員名"
            disabled="true"
            outlined
            dense
          />
          <!-- 職種区分 -->
          <a-label-select
            vid="occupation"
            ref="occupation"
            v-model="occupation"
            label="職種"
            outlined
            dense
            disabled="true"
            :items="occupationList"
          />
          <!-- 専門職種区分 -->
          <a-label-select
            vid="proOccupations"
            ref="proOccupations"
            v-model="proOccupations"
            label="専門職種"
            multiple
            outlined
            dense
            :items="proOccupationList"
          />
          <!-- スキル -->
          <a-label-select
            vid="skills"
            ref="skills"
            v-model="skills"
            label="スキル"
            multiple
            outlined
            dense
            :items="skillList"
          />
          <!-- 自己PR -->
          <a-field-label> {{ "自己PR" }} </a-field-label>
          <v-textarea
            vid="selfPromotion"
            ref="selfPromotion"
            v-model="selfPromotion"
            outlined
          />
            </div>
          </div>
        </v-card-text>
        <v-card-actions>
          <v-btn
            color="primary"
            :loading="isLoading"
            @click="save"
          >
            {{ $t("common.save") }}
          </v-btn>
        </v-card-actions>
    </v-card>
  </div>
</template>

<script>
import { mapState } from 'vuex'

/**
 * ロールマスタの詳細画面です。
 */
export default {
  head: {
    title () {
      return {
        inner: this.$t('app.productName'),
        complement: this.$t('ZAD0040.title')
      }
    }
  },
  props: ['mode', 'routeId'],
  data () {
    return {
      // 状態
      isLoading: false,

      // フォーム
      employeeNo: '0095',
      userName: '水野 伸一',
      occupation: 'REGULAR_TECHNICAL',
      proOccupations: ['PM', 'PL'],
      lengthOfService: 16,
      skills: ['PRODUCTION_MANAGEMENT', 'PRODUCTION_PREPARATION'],
      occupationList: [
        {
          value: 'GENERAL_TECHNICAL',
          text: '一般技術職'
        },
        {
          value: 'REGULAR_TECHNICAL',
          text: '総合技術職'
        }
      ],
      proOccupationList: [
        {
          value: 'PM',
          text: 'プロジェクトマネージャー'
        },
        {
          value: 'PL',
          text: 'プロジェクトリーダー'
        },
        {
          value: 'SE',
          text: 'システムエンジニア'
        },
        {
          value: 'PG',
          text: 'プログラマー'
        }
      ],
      selfPromotion: '入社16年目とのことです。すごいです。',
      skillList: [
        {
          value: 'PRODUCTION_MANAGEMENT',
          text: '生産管理'
        },
        {
          value: 'PRODUCTION_PREPARATION',
          text: '生産準備'
        }
      ]
    }
  },
  computed: {
    ...mapState('app', ['locale']),
    breadcrumbs () {
      return [
        {
          text: 'フィルターサンプル',
          to: { name: 'filterSample' },
          exact: true
        },
        {
          text: '参照',
          disabled: true
        }
      ]
    },
    permissionsTableHeaders () {
      return [
        { text: this.$t('ZAD0040.permissionName'), value: 'permissionName' },
        { text: this.$t('ZAD0040.permissionCd'), value: 'permissionCd' }
      ]
    },
    isNewMode () {
      return this.mode === 'new'
    },
    isEditMode () {
      return this.mode === 'edit'
    }
  },
  methods: {
    /**
     * 画面の再描画を行います
     */
    async refresh () {
      // 画面表示用の情報を取得
      this.fetchScreenData()

      // 更新モードの場合は現在の登録情報を取得
      if (this.isEditMode) {
        this.id = this.routeId
        this.fetchData()
      }
    },

    /**
     * 画面表示用の情報を取得します。
     */
    async fetchScreenData () {
      try {
        // ローディングを設定
        this.isLoading = true

        // 権限一覧を取得
        const response = await this.$axios.get('/permissions')

        // 画面に結果をセット
        this.permissionList = response.data
      } catch (error) {
        // エラーメッセージを表示
        this.$setFormErrors(this.$refs.observer, error)
        this.$store.dispatch('app/catchError', error)
      } finally {
        // ローディングを解除
        this.isLoading = false
      }
    },

    /**
     * データを取得します
     */
    async fetchData () {
      try {
        // ローディングを設定
        this.isLoading = true

        // データを取得
        const response = await this.$axios.get(`/admin/roles/${this.id}`)
        this.setFetchedData(response.data)
      } catch (error) {
        // エラーの場合は404ページに移動
        this.$router.replace({ name: 'error-not-found' })
      } finally {
        // ローディングを解除
        this.isLoading = false
      }
    },

    /**
     * 保存処理を行います。
     */
    async save () {
      try {
        // ローディングを設定
        this.isLoading = true

        /// 登録処理
        let response = null
        if (this.isNewMode) {
          response = await this.saveNew()

          // 完了メッセージを表示
          this.$store.dispatch('app/showSuccess', {
            message: 'messages.info.success',
            args: ['common.save']
          })
          this.$router.replace({
            name: 'ZAD0040-detail',
            params: { id: response.data.id }
          })
        } else if (this.isEditMode) {
          response = await this.saveUpdate()

          // 完了メッセージを表示
          this.$store.dispatch('app/showSuccess', {
            message: 'messages.info.success',
            args: ['common.save']
          })

          this.setFetchedData(response.data)
        }
      } catch (error) {
        // エラーメッセージを表示
        this.$setFormErrors(this.$refs.observer, error)
        this.$store.dispatch('app/catchError', error)
      } finally {
        // ローディングを解除
        this.isLoading = false
      }
    },

    /**
     * 新規登録を行います
     */
    async saveNew () {
      // リクエストを作成
      const permissions = this.selectedPermissions.map((p) => p.permissionCd)
      const request = {
        roleName: this.roleName,
        description: this.description,
        permissions
      }

      // 登録処理
      return await this.$axios.post('/admin/roles/', request)
    },

    /**
     * 更新を行います
     */
    async saveUpdate () {
      // リクエストを作成
      const permissions = this.selectedPermissions.map((p) => p.permissionCd)
      const request = {
        id: this.id,
        roleNameTid: this.roleNameTid,
        roleName: this.roleName,
        descriptionTid: this.descriptionTid,
        description: this.description,
        isActive: this.isActive,
        permissions
      }

      return await this.$axios.patch(`/admin/roles/${this.id}`, request)
    },

    /**
     * 画面にレスポンス情報をセットする
     */
    setFetchedData (data) {
      this.id = data.id
      this.roleNameTid = data.roleNameTid
      this.roleName = data.roleName || {}
      this.descriptionTid = data.descriptionTid
      this.description = data.description || {}
      this.isActive = data.isActive

      // 権限テーブル選択をセット
      this.selectedPermissions = data.permissions
        .map(rp => {
          return this.permissionList.find((p) => p.permissionCd === rp)
        })
    }
  }
}
</script>
